        const FPS = 15;
        let frameIdx = 0;
        let playing = true;
        let interval: ReturnType<typeof setInterval>;

        // RLE Decompressor
        const decompress = (str: string) => {
            return str.replace(/~(\d+)~(.)/g, (match, count, char) => {
                return char.repeat(parseInt(count));
            });
        };

        // Pre-decompress first frame to set size
        if(preRef.current && data.length > 0) {
             preRef.current.innerText = decompress(data[0]);
        }

        const play = () => {
            if(!playing) return;
            
            interval = setInterval(() => {
                if (!preRef.current) return;
                
                if(frameIdx >= data.length) frameIdx = 0; // Loop
                preRef.current.innerText = decompress(data[frameIdx]);
                frameIdx++;
            }, 1000 / FPS);
        };

        play();
        
        return () => {
            if (interval) clearInterval(interval);
        }
